const TurnWidget = () => {
  const [state, { decreaseTimeLeft, changeCurrentPlayer }] = useGameBoard();

  createEffect((prevTimerIsPaused) => {
    if (state.timer.timeLeft < 0) {
      changeCurrentPlayer();
    } else if (prevTimerIsPaused) {
      if (!state.timer.paused) {
        decreaseTimeLeft();
      }
    } else if (!state.timer.paused) {
      const timer = setTimeout(() => {
        decreaseTimeLeft();
      }, 1000);
      onCleanup(() => clearTimeout(timer));
    }
    return state.timer.paused;
  }, state.timer.paused);

  return (
    <div class="absolute left-1/2 top-[320px] z-20 -translate-x-1/2">
      <div class="relative">
        <Show
          fallback={<TurnWidgetSvg class="text-pastel-yellow" />}
          when={state.currentPlayer === 1}
        >
          <TurnWidgetSvg class="text-salmon-pink" />
        </Show>
        <div
          class={cn(
            "absolute left-1/2 top-1/2 flex w-full -translate-x-1/2 -translate-y-1/2 flex-col gap-1",
            state.currentPlayer === 1 ? "text-white" : "text-black",
          )}
        >
          <Typography class="text-center uppercase" intent="h4">
            {`Player ${state.currentPlayer}'s turn`}
          </Typography>
          <Typography intent="h1" class="text-center uppercase">
            {`${state.timer.timeLeft}s`}
          </Typography>
        </div>
      </div>
    </div>
  );
};
